mapper.select:
SELECT q_no, q_state, q_title, q_content, TO_CHAR(q_date, 'YYYY.MM.DD') as q_date, u_id, q_answer FROM faq_admin WHERE q_no in(SELECT q_no FROM (SELECT rownum as rnum, q_no FROM (SELECT q_no FROM faq_admin nnb
where u_id=#{u_id}
ORDER BY q_date DESC)) WHERE rnum BETWEEN #{startRowNo} AND #{endRowNo})ORDER BY q_date DESC

